import { Resizable, Size, Enable } from "re-resizable";

interface ResizableProps {
  direction: 'horizontal' | 'vertical';
  children?: React.ReactNode;
}

interface ResizableBoxProps {
  defaultSize: Size | undefined,
  size: Size | undefined,
  enable: Enable | undefined,
  maxWidth: number | string,
  maxHeight: number | string 
}

const style = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  border: "solid 1px #ddd",
  background: "#f0f0f0"
} as const;

const enable: Enable = {
  top:false,
  right:true, 
  bottom:false, 
  left:false, 
  topRight:false, 
  bottomRight:false, 
  bottomLeft:false, 
  topLeft:false
}

const ResizableBox: React.FC<ResizableProps> = ({ direction, children }) => {

  if (direction === 'horizontal'){
    resizableProps = {
      className: 'resize-horizontal',
      maxConstraints: [innerWidth * 0.975, Infinity], 
      height: Infinity, 
      width,
      resizeHandles:['e'],
      onResizeStop: (event, data) => {
        setWidth(data.size.width);
      }
    };
  } else {
    resizableProps = {
      maxConstraints: [Infinity, innerHeight * 0.975], 
      height: 300, 
      width:Infinity,
      resizeHandles:['s']
    };
  }
  
  return (
  <Resizable 
    style={style} 
    defaultSize={{
      width: 200,
      height: 200
    }}
    enable={enable}
    >
    hello resizable
  </Resizable>
  );

};

export default ResizableBox;
